package com.swipehire.swipehirebackend;import jakarta.servlet.http.HttpServletRequest;import org.springframework.security.web.csrf.CsrfToken;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;import java.util.List;@RestControllerpublic class UserController {  private List<User> users = new ArrayList<>(List.of(    new User("Alex", 20, 1),    new User("Era", 24, 2)  ));  @GetMapping("/users")  public List<User> getUsers(){    return users;  }  @GetMapping("/csrf-token")  public CsrfToken getCsrfToken(HttpServletRequest request){    return (CsrfToken) request.getAttribute("_csrf");  }  @PostMapping("/users")  public User addUser(@RequestBody User user){    users.add(user);    return user;  }}