package com.swipehire.swipehirebackend.controller;import com.swipehire.swipehirebackend.model.Users;import com.swipehire.swipehirebackend.repo.UserRepo;import com.swipehire.swipehirebackend.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class UserController {  @Autowired  private BCryptPasswordEncoder passwordEncoder;  @Autowired  private UserService service;  private UserRepo repo;  @PostMapping("/register")  public Users register(@RequestBody Users user){    return service.register(user);  }  @PostMapping("/login")  public ResponseEntity<String> login(@RequestBody Users user) {    Users found = repo.findByUsername(user.getUsername());    if (found == null || !passwordEncoder.matches(user.getPassword(), found.getPassword())) {      return ResponseEntity.status(HttpStatus.UNAUTHORIZED).body("Invalid credentials");    }    return ResponseEntity.ok("Success");  }}