package com.swipehire.swipehirebackend.service;import com.swipehire.swipehirebackend.model.Users;import com.swipehire.swipehirebackend.repo.UserRepo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class UserService {  @Autowired  private UserRepo repo;  public Users register(Users user){    return repo.save(user);  }}